window.addEventListener("load", function () {
	var cmdLine = window.arguments[0];
	cmdLine = cmdLine.QueryInterface(Components.interfaces.nsICommandLine);
	var width = cmdLine.handleFlagWithParam("width", false);
	var height = cmdLine.handleFlagWithParam("height", false);
	var url = cmdLine.handleFlagWithParam("url", false);
	var icon = cmdLine.handleFlagWithParam("icon", false);

	window.resizeTo(width, height);

	var browser = document.getElementById('content');
	browser.hhomepage = url;
	browser.loadURI(url);
	document.getElementById('icon').href = icon;

	//var win = document.getElementById('node-firefox-driver');
	setTimeout(function() {
		//alert(document.getElementById('content').src);
	//	alert(window.outerWidth+'x'+window.outerHeight);
	}, 1000);
}, false);