var firefox = require('../');
firefox.open(
	'http://www.comlog.org',
	{width:800, height:600},
	function(err, ps) {
	if (err) console.error(err);

	if (ps) {
		ps.stdout.on('data', function (data) {
			console.log(data);
		});

		ps.stderr.on('data', function (data) {
			console.log('ps stderr: ' + data);
		});
	}
});